    "on Monday": { day: "Monday" },
    "on Tuesday": { day: "Tuesday" },
    "on Wednesday": { day: "Wednesday" },
    "on Thursday": { day: "Thursday" },
    "on Friday": { day: "Friday" },
	"on Saturday": { day: "Saturday" },
	"on Sunday": { day: "Sunday" },

	"at 5": { time: "5:00" },
	"at 6": { time: "6:00" },
    "at 7": { time: "7:00" },
    "at 8": { time: "8:00" },
    "at 9": { time: "9:00" },
    "at 10": { time: "10:00" },
    "at 11": { time: "11:00" },
    "at 12": { time: "12:00" }
}

const grammar2: { [index: string]: boolean } = {
    "yes of course": true,
    "sure": true,
    "absolutely": true,
    "yes": true,
    "no way": false,
    "no": false
}

let a = grammar2["yes"]
let b = grammar2["no"]

function promptAndAsk(prompt: string): MachineConfig<SDSContext, any, SDSEvent> {
    return ({
        initial: 'prompt',
        states: {
            prompt: {
                entry: say(prompt),
                on: { ENDSPEECH: 'ask' }
            },
            ask: {
                entry: send('LISTEN')
            },
        }})
}


